mapper.select:
select c.cert_num as sfzh,c.name as xm ,c.BATCHNO as batchno,c.CARDSN as cardsn,b.boxno as boxNo,x.binno as binNo, c.REGIS_STATUS as regisStatus,c.ORGADDRESS as orgAddress from bus_rcm_card c left join bus_rcm_box b on c.box_id = b.id left join bus_rcm_bin x on b.bin_id = x.id where c.cert_num=#{sfzh}
AND c.name=#{xm}
order by MOD_DATE

select id,CERT_NUM as sfzh ,NAME as name,SO_CARD_NUM as sbkh,BANK_ACCOUNT,BANK_NAME, CARDSN,STATUS,GRANT_TIME,GRANT_USER_ID,GRANT_NET_TYPE, GRANT_NET_ID,BOX_ID,CARD_ID,ADD_USERNO,ADD_DATE,MOD_USERNO, MOD_DATE,RETEN_TIME,REASON,OTHER_REASON,REGIS_STATUS as regisStatus, orgaddress as orgName,phone,orgid as orgId,ADDUSER as adduser,make_card_time as makeCardTime from bus_rcm_card WHERE 1=1
AND cert_num = #{sfzh}
AND SO_CARD_NUM=#{sbkh}

SELECT id as orgId ,address as orgAddress,SO_NAME as orgName FROM  BASIC_CONF_SOCIAL_ORG WHERE 1=1

  and  id = #{orgId}

order by orgId
