mapper.update:
update T_YTH_PICTURE_INFO set UPDATE_TIME=sysdate
,PIC_PATH=#{picPath}
where PIC_ID=#{picId}

update T_YTH_PICTURE_BUS set UPDATE_TIME=sysdate
,PIC_STATUS=#{picStatus}
,PIC_TYPE=#{picType}
,PIC_MESSAGE=#{picMessage}
where PIC_ID=#{picId}

update T_YTH_BASIC_PERSON_INFO set UPDATE_TIME=sysdate
,SEX=#{sex}
,NATION=#{nation}
,DOMICILE=#{domicile}
,PHONE=#{phone}
,SBKH=#{sbkh}
,BIRTHDAY=to_date(#{birthday},'yyyy-MM-dd')
where sfzh=#{sfzh}

mapper.select:
SELECT p.person_id as personId,p.xm,p.sfzh,p.sex,p.nation,p.sbkh,p.birthday,p.domicile,p.phone
FROM T_YTH_BASIC_PERSON_INFO p
where 1=1 AND p.sfzh = #{sfzh}

    AND p.xm = #{xm}

select s.SSSM_ORG_ID as orgId,s.SSSM_BANK_ID as  csspBankId   from T_YTH_DEVICE_SSSM s
 where  1=1 and s.deviceno= (
    select deviceno from T_YTH_DEVICE d  where  1=1  and equipment_no=#{deviceId}
 )

SELECT t.id,t.name,t.address,t.longitude,t.latitude,t.telephone,t.traffic, GetDistance(#{longitude}, #{latitude},t.longitude,t.latitude) distance FROM t_yth_branch t left join T_YTH_BRANCH_SERVICE p on (t.id = p.branch_id) where p.services_code = #{servicesCode} and t.longitude!=0 and t.latitude!=0
AND t.SERVICE_BANK_CODE = #{orgCode}
AND t.DISTINCT_CODE = #{areaCode}
ORDER BY distance ASC

select c.longitude,c.latitude from T_YTH_DEVICE_COORD c
 where  1=1 and c.deviceno= (
    select deviceno from T_YTH_DEVICE d  where  1=1  and equipment_no=#{deviceId}
 )

select o.name as name, o.org_code as orgCode from t_org t ,t_org o where o.parent_org_id=t.org_id 

     and t.org_code=#{orgCode}


     and o.distinct_id=#{areaCode}
