(select distinct(batch_no) from card_batch where id = t.batch_id) batch_no,
(select distinct(bank) from card_batch where id = t.batch_id) bank
--,(select count(*) from CARD_BATCH_DETAIL d where d.batch_binbox_id=t.id) cardNum
from CARD_BATCH_BINBOX t
) --where t.status='00')
where batch_id in (select distinct id from card_batch where fb_status='01')
and bank=#{bank}
and (qy in

    #{qy}

or qy='220200' or qy='220000')

    and batch_id=(select id from CARD_BATCH where batch_no=#{batchNo})


    and bin=#{bin}


    and box=#{box}


    and status=#{status}

select * from (select batch_id,t.bin ,t.qy, (select distinct(batch_no) from card_batch where id = t.batch_id) batch_no, (select count(*) from CARD_BATCH_BINBOX c where c.batch_id=t.batch_id and c.bin=t.bin and c.qy=t.qy and c.status='00') box, (select distinct(bank) from card_batch where id = t.batch_id) bank --,(select count(*) from CARD_BATCH_DETAIL d where d.batch_binbox_id in (select b.id from CARD_BATCH_BINBOX b where b.status='00' and b.batch_id=t.batch_id and b.bin=t.bin and b.qy=t.qy)) cardNum from CARD_BATCH_BINBOX t where t.status='00' group by t.batch_id,t.qy,t.bin) where batch_id in (select distinct id from card_batch where fb_status='01') and bank=#{bank} and (qy in
#{qy}
or qy='220200' or qy='220000')
and batch_id=(select id from CARD_BATCH where batch_no=#{batchNo})
and bin=#{bin}

mapper.select:
select seq_card_info.nextval as id,d.batch_id,d.batch_binbox_id,
d.name,d.idcard,d.cardid,d.batch_no,d.company_code,
d.company_name,d.name as angent,d.phone,d.address,d.kg_zxwz,b.rkwd as fkwd,d.bank,d.city,
d.xt_zxwz
from CARD_BATCH_DETAIL d,CARD_BATCH_BINBOX b
where
b.status='00' and b.batch_id=d.batch_id and b.id=d.BATCH_BINBOX_ID

and d.batch_no=#{batchNo}


and REGEXP_SUBSTR(d.kg_zxwz,'[^-]+',1,2,'i')=#{bin}



and REGEXP_SUBSTR(d.kg_zxwz,'[^-]+',1,2,'i')=#{bin}
and REGEXP_SUBSTR(d.kg_zxwz,'[^-]+',1,3,'i')=#{box}

select t.batch_no,b.bin,b.box
from CARD_BATCH t,CARD_BATCH_BINBOX b
where t.id=b.batch_id and b.status='00' and t.batch_no=#{batchNo}

mapper.update:
update CARD_BATCH_BINBOX set status='01',rkwd=#{fkwd},ccid=#{ccaid},update_time=sysdate where
status='00' and

batch_id=(select distinct(id) from card_batch where batch_no = #{batchNo})


and bin=#{bin}



and bin=#{bin}
and box=#{box}

mapper.insert:
insert into CARD_INFO (ID,NAME,IDCARD,CARDID,BATCH_NO,COMPANY_CODE, COMPANY_NAME,ANGENT,PHONE,ADDRESS,OLD_CFWZ,STATUS,FKWD,BANK,REGINALCODE, INPUT_TIME,XT_ZXWZ) select A.* from(
SELECT #{item.id},#{item.name},#{item.idcard},#{item.cardid},#{item.batchNo},#{item.companyCode}, #{item.companyName},#{item.angent},#{item.phone},#{item.address},#{item.kgZxwz},2,#{item.fkwd},#{item.bank},#{item.city}, sysdate,#{item.xtZxwz} FROM DUAL
) A

insert into card_store (ID,csid,ciid,ccid,store_time,bin,box) select seq_card_store.nextval,A.* from(
SELECT REGEXP_SUBSTR(#{item.kgZxwz},'[^-]+',1,4,'i'), #{item.id},#{item.ccaid},sysdate,REGEXP_SUBSTR(#{item.kgZxwz},'[^-]+',1,2,'i'), REGEXP_SUBSTR(#{item.kgZxwz},'[^-]+',1,3,'i') FROM DUAL
) A

insert into CARD_BATCH_LOG t (t.id,t.userid,t.batch_id,t.bus_type,t.BATCH_BINBOX_ID,t.create_time) select seq_card_batch_log.nextval,A.* from(
SELECT #{item.userid},#{item.batchId},'03',#{item.batchBinBoxId}, sysdate FROM DUAL
) A

mapper.select:
select * from (
select t.id,batch_id,t.bin,box,t.qy,t.rkwd,t.update_time,
(select distinct(batch_no) from card_batch where id = t.batch_id) batch_no,
(select distinct(bank) from card_batch where id = t.batch_id) bank
from CARD_BATCH_BINBOX t
where t.status='01')
WHERE bank=#{bank}
and (qy in

    #{qy}

or qy='220200' or qy='220000')

    and batch_no=#{batchNo}


    and bin=#{bin}


    and box=#{box}

mapper.update:
update CARD_INFO t set t.fkwd=#{fkwd},t.xt_zxwz=t.old_cfwz where
t.batch_no=#{batchNo}
and REGEXP_SUBSTR(t.old_cfwz,'[^-]+',1,2,'i')=#{bin}
and REGEXP_SUBSTR(t.old_cfwz,'[^-]+',1,3,'i')=#{box}

update card_store s set
s.ccid=#{ccaid},
s.bin=#{bin},
s.box=#{box}
where s.ciid in(
select id from CARD_INFO t where
t.batch_no=#{batchNo}
and REGEXP_SUBSTR(t.old_cfwz,'[^-]+',1,2,'i')=#{bin}
and REGEXP_SUBSTR(t.old_cfwz,'[^-]+',1,3,'i')=#{box}
)

if.if:
select distinct(b.bin) from CARD_BATCH_BINBOX b,CARD_BATCH c where b.status in('00','01') and b.batch_id=(select id from CARD_BATCH where batch_no=#{batchNo}) and c.id=b.batch_id and c.bank=#{bank} and (qy in
#{qy}
or qy='220200' or qy='220000') order by to_number(b.bin)

