mapper.select:
select sum(card_instore_num)as cardInstoreNum,sum(card_grant_num)as cardGrantNum,sum(card_surplus_num)as cardSurplusNum, sum(recovery_num)as recoveryNum,sum(waste_card_num)as wasteCardNum,sum(cancel_num)as cancelNum, o.parent_org_id,(select name from sisp_public.t_org where org_id=o.parent_org_id)as name from tempcard_info i,sisp_public.t_org o where i.rkwd=o.org_id and o.parent_org_id !=0
and o.parent_org_id=#{bank}
group by o.parent_org_id

select sum(card_instore_num)as cardInstoreNum,sum(card_grant_num)as cardGrantNum,sum(card_surplus_num)as cardSurplusNum, sum(recovery_num)as recoveryNum,sum(waste_card_num)as wasteCardNum,sum(cancel_num)as cancelNum, o.parent_org_id,(select name from sisp_public.t_org where org_id=o.parent_org_id)as name from tempcard_info i,sisp_public.t_org o where i.rkwd=o.org_id and o.parent_org_id !=0
and o.distinct_id=#{distinctId}
and o.parent_org_id=#{bank}
group by o.parent_org_id

select i.*,o.name from tempcard_info i,sisp_public.t_org o
where i.rkwd=o.org_id
and o.parent_org_id=#{rkwd}

select i.*,o.name from tempcard_info i,sisp_public.t_org o
where i.rkwd=o.org_id

    and i.rkwd=#{rkwd}

select o.name,s.makeCardCount,s.grantCardCount,s.tempGrantCardCount,s.nowGrantCardCount from ( select t10.bank,count(1) as makeCardCount, nvl( (select tt.grantCardCount from (select count(1)as grantCardCount,t11.bank from ( select t1.* from CARD_INFO t1,sisp_public.t_org i1 where t1.fkwd=i1.org_code and t1.status in(6,7) and t1.sync_status is null
and i1.distinct_id = #{distinctId}
and t1.activetime >= to_date(#{beginTime}, 'yyyy-mm-dd hh24:mi:ss')
and t1.activetime <= to_date(#{endTime}, 'yyyy-mm-dd hh24:mi:ss')
)t11 group by t11.bank)tt where tt.bank=t10.bank),0) as grantCardCount, nvl( (select tt2.tempGrantCardCount from (select count(1)as tempGrantCardCount,t12.bank from ( select t2.*,b2.bank from tempcard_detail t2,tempcard_box b2,sisp_public.t_org i2 where t2.rkwd=b2.rkwd and t2.rkwd=i2.org_id and t2.status='01'
and i2.distinct_id = #{distinctId}
and t2.update_TIME >= to_date(#{beginTime}, 'yyyy-mm-dd hh24:mi:ss')
and t2.update_TIME <= to_date(#{endTime}, 'yyyy-mm-dd hh24:mi:ss')
)t12 group by t12.bank)tt2 where tt2.bank=t10.bank ),0)as tempGrantCardCount, nvl( (select tt3.nowGrantCardCount from (select count(1)as nowGrantCardCount,t13.bank from ( select t3.* from replacecard_detail t3,sisp_public.t_org i3 where t3.rkwd=i3.org_code and t3.status in('01','03')
and i3.distinct_id = #{distinctId}
and t3.faka_time >= to_date(#{beginTime}, 'yyyy-mm-dd hh24:mi:ss')
and t3.faka_time <= to_date(#{endTime}, 'yyyy-mm-dd hh24:mi:ss')
)t13 group by t13.bank)tt3 where tt3.bank=t10.bank ),0)as nowGrantCardCount from ( select t.* from CARD_INFO t ,sisp_public.t_org i where t.fkwd=i.org_code
and i.distinct_id = #{distinctId}
and t.input_time >= to_date(#{beginTime}, 'yyyy-mm-dd hh24:mi:ss')
and t.input_time <= to_date(#{endTime}, 'yyyy-mm-dd hh24:mi:ss')
)t10 group by t10.bank )s,sisp_public.t_org o where s.bank=o.org_code order by s.grantCardCount desc
